package com.android.own.instag.data.repositoryimport com.android.own.instag.data.model.Userimport com.android.own.instag.data.remote.NetworkServiceimport com.android.own.instag.data.remote.response.ImageResponseimport io.reactivex.Singleimport okhttp3.MediaTypeimport okhttp3.MultipartBodyimport okhttp3.RequestBodyimport java.io.Fileimport javax.inject.Injectclass ProfileUploadRepository @Inject constructor(    private val networkService: NetworkService) {    fun uploadVaccinePhoto(        imageFile: File, user: User    ): Single<ImageResponse>? {        return MultipartBody.Part.createFormData(            "image", imageFile.name,            RequestBody.create(MediaType.parse("image/*"), imageFile)        ).run {            return@run networkService.doImageUpload(this, user.id, user.accessToken)                .map { it }        }    }}